        ? { 
            ...quest, 
            status: quest.status === 'active' ? 'paused' as const : 'active' as const 
          }
        : quest
    )

    setQuests(updatedQuests)
    QuestStorage.saveQuests(updatedQuests)
  }

  const editQuest = (quest: Quest) => {
