          onClose={() => {
            setIsModalOpen(false)
            setEditingQuest(undefined)
          }}
          title={editingQuest ? 'Edit Quest' : 'Create New Quest'}
          className="max-w-2xl"
        >
          <QuestForm
            quest={editingQuest}
            onSave={saveQuest}
            onCancel={() => {
              setIsModalOpen(false)
              setEditingQuest(undefined)
            }}
          />
        </Modal>
      </div>
    </div>
  )
}

