                ]}
                value={filters.priority || ''}
                onChange={(e) => setFilters(prev => ({ ...prev, priority: e.target.value as 'low' | 'medium' | 'high' | 'critical' | undefined }))}
              />

              <Select
                options={[
                  { value: 'createdAt', label: 'Sort by Date' },
