@tailwind base;
@tailwind components;
@tailwind utilities;

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html,
body {
  max-width: 100vw;
  overflow-x: hidden;
  font-family: 'Inter', 'Space Grotesk', sans-serif;
}

body {
  background: linear-gradient(135deg, #0A0A0A 0%, #1A1A1A 50%, #0A0A0A 100%);
  color: #FFFFFF;
  min-height: 100vh;
}

/* Custom scrollbar for webkit browsers */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: #1A1A1A;
}

::-webkit-scrollbar-thumb {
  background: #00FFFF;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #00CCCC;
}

/* Gaming UI Elements */
@layer components {
  .gaming-card {
    @apply bg-gaming-card border border-gaming-border rounded-xl p-6 shadow-lg transition-all duration-300;
  }

  .gaming-card:hover {
    @apply border-primary-400/50 shadow-xl shadow-primary-400/10;
  }

  .gaming-button {
    @apply bg-gradient-cyan text-gaming-bg font-gaming font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:shadow-lg hover:shadow-primary-400/25 active:scale-95;
  }

  .gaming-input {
    @apply bg-gaming-card border border-gaming-border text-text-primary placeholder-text-muted rounded-lg px-4 py-3 focus:border-primary-400 focus:ring-2 focus:ring-primary-400/20 transition-all duration-300;
  }

  .xp-badge {
    @apply bg-gradient-accent text-white px-3 py-1 rounded-full text-sm font-mono font-semibold shadow-lg;
  }

  .quest-complete {
    @apply animate-pulse bg-green-500/20 border-green-400 text-green-400;
  }

  .quest-overdue {
    @apply animate-pulse bg-red-500/20 border-red-400 text-red-400;
  }

  .neon-text {
    @apply text-primary-400;
    text-shadow: 0 0 10px #00FFFF, 0 0 20px #00FFFF, 0 0 40px #00FFFF;
  }

  .gaming-gradient {
    background: linear-gradient(135deg, #00FFFF 0%, #008B8B 50%, #1E90FF 100%);
    background-size: 200% 200%;
    animation: gradientShift 3s ease infinite;
  }
}

@keyframes gradientShift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

